"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[1477],{10:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"css-resource","metadata":{"permalink":"/blog/css-resource","source":"@site/blog/2023-03-08-css-resource.mdx","title":"CSS \u5b78\u7fd2\u8cc7\u6e90","description":"\u4ee5\u4e0b\u662f\u5728\u7814\u7a76 CSS \u5404\u7a2e\u5149\u602a\u9678\u96e2\u7684\u554f\u984c\u548c\u6548\u679c\u7684\u8def\u4e0a\uff0c\u641c\u96c6\u5230\u4e00\u4e9b\u7528\u4f86\u67e5\u627e\u8cc7\u6599\uff0c\u6216\u662f\u89c0\u6469\u5927\u795e\u7bc4\u4f8b\u7684\u8cc7\u6e90\uff1a","date":"2023-03-08T00:00:00.000Z","formattedDate":"March 8, 2023","tags":[{"label":"css","permalink":"/blog/tags/css"},{"label":"resource","permalink":"/blog/tags/resource"}],"readingTime":2.445,"hasTruncateMarker":false,"authors":[{"name":"Zoe Huang","title":"F2E x Sweetholic x Backpacker","url":"https://typeii8.github.io","imageURL":"/img/avatar.jpg","key":"zoe"}],"frontMatter":{"slug":"css-resource","title":"CSS \u5b78\u7fd2\u8cc7\u6e90","authors":"zoe","tags":["css","resource"]},"nextItem":{"title":"Frontend \u5b78\u7fd2\u8cc7\u6e90","permalink":"/blog/f2e-resource"}},"content":"\u4ee5\u4e0b\u662f\u5728\u7814\u7a76 CSS \u5404\u7a2e\u5149\u602a\u9678\u96e2\u7684\u554f\u984c\u548c\u6548\u679c\u7684\u8def\u4e0a\uff0c\u641c\u96c6\u5230\u4e00\u4e9b\u7528\u4f86\u67e5\u627e\u8cc7\u6599\uff0c\u6216\u662f\u89c0\u6469\u5927\u795e\u7bc4\u4f8b\u7684\u8cc7\u6e90\uff1a\\n\\n\\n## \u7db2\u7ad9\\n\\n[CSS-Tricks - Tips, Tricks, and Techniques on using Cascading Style Sheets.](https://css-tricks.com/)\\n\\n:::note\\nCSS-Tricks \u6211\u6703\u7528\u4f86\u770b\u4e00\u4e9b\u65b0\u8a9e\u6cd5\u7684\u4ecb\u7d39\u548c\u7bc4\u4f8b\u5beb\u6cd5\uff0c\u57fa\u672c\u5b9a\u7fa9\u53ef\u4ee5\u67e5\u627e [W3C](https://www.w3schools.com/cssref/index.php) \u6216 [MDN](https://developer.mozilla.org/zh-TW/)\uff0c\u4f46\u5982\u679c\u662f\u60f3\u8981\u770b\u4e00\u4e9b\u57fa\u790e\u5230\u9032\u968e\u7684\u5be6\u4f5c\u61c9\u7528\uff0c\u6216\u662f\u4e00\u4e9b\u9177\u70ab\u6548\u679c\u7684\u7bc4\u4f8b\uff0c\u6211\u5c31\u6703\u4f86\u9019\u908a\u6316\u6316\u5bf6\u3002\\n\\n\u7576\u7136\u7db2\u7ad9\u88e1\u4e0d\u50c5\u50c5\u53ea\u6709 CSS \u4e3b\u984c\uff0c\u9084\u6709\u8a31\u591a\u8ddf\u524d\u7aef\u76f8\u95dc\u7684\u5176\u4ed6\u4e3b\u984c\uff0c\u6587\u7ae0\u66f4\u65b0\u7684\u983b\u7387\u548c\u8cea\u91cf\u90fd\u5f88\u597d\u3002\\n:::\\n\\n\\n- [Smashing Magazine \u2014 For Web Designers And Developers](https://www.smashingmagazine.com/)\\n- [Codrops](https://tympanus.net/codrops/)\\n\\n:::note\\nSmashing Magazine \u548c Codrops \u4e5f\u662f\u6211\u6703\u62ff\u4f86\u89c0\u6469\u4e00\u4e9b\u7279\u6b8a\u6548\u679c\u3001\u6216\u662f\u67e5\u627e\u9032\u968e\u61c9\u7528\u548c\u65b0\u77e5\u8b58\u7684\u8cc7\u6e90\u3002\\n\\n\u6709\u4e9b\u5be6\u4f5c\u4e0d\u898b\u5f97\u80fd\u5b8c\u5168\u89e3\u6c7a\u81ea\u5df1\u9047\u5230\u7684\u554f\u984c\uff0c\u7562\u7adf\u5be6\u969b\u958b\u767c\u7522\u54c1\u7684\u904e\u7a0b\u4e2d\uff0c\u9084\u662f\u6703\u9047\u5230\u5411\u4e0b\u76f8\u5bb9\u6027\u6216\u662f\u5ba2\u6236\u9700\u6c42\u7b49\u9650\u5236\uff0c\u6700\u65b0\u6280\u8853\u6700\u9177\u70ab\u7684\u6548\u679c\u5f80\u5f80\u5230\u6700\u5f8c\u6703\u88ab\u95b9\u5272\u6216\u662f(\u53d6)\u6368\uff0c\u5728\u770b\u9019\u4e9b\u6548\u679c\u7bc4\u4f8b\u6642\uff0c\u4e5f\u958b\u59cb\u6703\u601d\u8003\u5982\u4f55\u517c\u9867\u6548\u679c\u548c\u5f48\u6027/\u53ef\u8b80\u6027\uff0c\u9019\u5169\u500b\u7db2\u7ad9\u7684\u6587\u7ae0\u66f4\u65b0\u7684\u983b\u7387\u548c\u8cea\u91cf\u4e5f\u90fd\u5f88\u4e0d\u932f\uff0c\u9069\u5408\u6316\u5bf6\u3002\\n:::\\n\\n---\\n\\n## \u5de5\u5177\\n\\n[Muzli](https://muz.li/)\\n\\n:::note\\n\u81ea\u5b9a\u7fa9\u700f\u89bd\u5668\u9996\u9801\u5de5\u5177\uff0c\u53ef\u4ee5\u7121\u8166\u8a02\u95b1\u5b98\u65b9\u63d0\u4f9b\u7684\u8cc7\u6e90\uff0c\u4e5f\u53ef\u4ee5\u81ea\u5b9a\u7fa9\u81ea\u5df1\u559c\u6b61\u7684\u4f86\u6e90\u3002\u4f5c\u70ba\u5438\u6536\u65b0\u77e5\u548c\u7372\u53d6\u9748\u611f\u662f\u4e00\u500b\u6eff\u4e0d\u932f\u7684\u5de5\u5177\uff0c\u6642\u4e0d\u6642\u5237\u4e00\u4e0b\u9806\u773c\u7684\u6587\u7ae0\uff0c\u6c92\u7a7a\u7684\u6642\u5019\uff0c\u4e00\u773c\u6383\u904e\u6587\u7ae0\u6a19\u984c\uff0c\u4e5f\u53ef\u4ee5\u5927\u6982\u77e5\u9053\u6700\u8fd1\u5728\u6d41\u884c\u4ec0\u9ebc\u4e3b\u984c\u3002\u9019\u500b\u5de5\u5177\u7684\u61c9\u7528\u4e0d\u50c5\u9650\u65bc\u6280\u8853\u6216UIUX\uff0c\u4e5f\u53ef\u4ee5\u8a02\u95b1\u570b\u5167\u5916\u65b0\u805e\u6216\u5176\u4ed6\u7db2\u7ad9\u7684RSS\uff0c\u63a8\u85a6\u5927\u5bb6\u53ef\u4ee5\u8a66\u8a66\u770b\uff0c\u4f5c\u70ba\u81ea\u5df1\u7684\u6574\u5408\u77e5\u8b58\u8cc7\u6e90\u5eab\u3002\\n:::"},{"id":"f2e-resource","metadata":{"permalink":"/blog/f2e-resource","source":"@site/blog/2023-03-07-f2e-resource.mdx","title":"Frontend \u5b78\u7fd2\u8cc7\u6e90","description":"\u5728\u5f9e UI \u8a2d\u8a08\u8f49\u8077\u524d\u7aef\u7684\u904e\u7a0b\u4e2d\uff0c\u66fe\u7d93\u5831\u540d\u904e\u5be6\u9ad4\u8ab2\uff0c\u4e5f\u4e0a\u904e\u4e00\u4e9b\u7dda\u4e0a\u8ab2\u7a0b\uff0c\u53c8\u6216\u662f\u627e youtube \u6559\u5b78\u5f71\u7247\u4f86\u770b\uff0c\u60f3\u5206\u4eab\u8b80\u904e\u89ba\u5f97\u4e0d\u932f\u7684\u514d\u8cbb\u5b78\u7fd2\u8cc7\u6e90\uff1a","date":"2023-03-07T00:00:00.000Z","formattedDate":"March 7, 2023","tags":[{"label":"js","permalink":"/blog/tags/js"},{"label":"resource","permalink":"/blog/tags/resource"}],"readingTime":5.87,"hasTruncateMarker":false,"authors":[{"name":"Zoe Huang","title":"F2E x Sweetholic x Backpacker","url":"https://typeii8.github.io","imageURL":"/img/avatar.jpg","key":"zoe"}],"frontMatter":{"slug":"f2e-resource","title":"Frontend \u5b78\u7fd2\u8cc7\u6e90","authors":"zoe","tags":["js","resource"]},"prevItem":{"title":"CSS \u5b78\u7fd2\u8cc7\u6e90","permalink":"/blog/css-resource"},"nextItem":{"title":"Git Memo","permalink":"/blog/git-memo"}},"content":"\u5728\u5f9e UI \u8a2d\u8a08\u8f49\u8077\u524d\u7aef\u7684\u904e\u7a0b\u4e2d\uff0c\u66fe\u7d93\u5831\u540d\u904e\u5be6\u9ad4\u8ab2\uff0c\u4e5f\u4e0a\u904e\u4e00\u4e9b\u7dda\u4e0a\u8ab2\u7a0b\uff0c\u53c8\u6216\u662f\u627e youtube \u6559\u5b78\u5f71\u7247\u4f86\u770b\uff0c\u60f3\u5206\u4eab\u8b80\u904e\u89ba\u5f97\u4e0d\u932f\u7684\u514d\u8cbb\u5b78\u7fd2\u8cc7\u6e90\uff1a\\n\\n## \u7dda\u4e0a\u8ab2\u7a0b\\n\\n[CS50: Introduction to Computer Science - Harvard](https://pll.harvard.edu/course/cs50-introduction-computer-science)\\n\\n:::note\\n\u7a0b\u5f0f\u6c92\u592a\u591a\u57fa\u790e\u7684\u4eba\u4e5f\u80fd\u770b\u61c2\u5438\u6536\u7684\u8ab2\u7a0b\uff0c\u975e\u5e38\u559c\u6b61\u8b1b\u8005\u7684\u6559\u5b78\uff0c\u8b1b\u8ab2\u904e\u7a0b\u5f88\u751f\u52d5\u3001\u5bcc\u6709\u611f\u67d3\u529b\uff0c\u8ab2\u7a0b\u4e2d\u4e5f\u8a2d\u8a08\u4e86\u8a31\u591a\u4e92\u52d5\u74b0\u7bc0\uff0c\u8ab2\u5802\u4e0a\u5927\u5bb6\u63d0\u554f\u5341\u5206\u8e34\u8e8d\uff0c\u770b\u73fe\u5834\u5b78\u751f\u63d0\u554f\u5f88\u6709\u8da3\uff0c\u56e0\u70ba\u5927\u5bb6\u4e0d\u898b\u5f97\u90fd\u662f\u672c\u79d1\uff0c\u4e5f\u4e0d\u6703\u5bb3\u7f9e\u89ba\u5f97\u81ea\u5df1\u63d0\u51fa\u7684\u554f\u984c\u662f\u4e0d\u662f\u592a\u7c21\u55ae\uff0c\u7e3d\u7684\u4f86\u8aaa\uff0c\u6211\u8a8d\u70ba\u9019\u662f\u4e00\u5802\u5f88\u80fd\u555f\u767c\u4eba\u7684\u8ab2\uff0c\u5f9e\u8ab2\u7a0b\u672c\u8eab\u5230\u4e0a\u8ab2\u65b9\u5f0f\u90fd\u6709\u4eae\u9ede\u3002\\n\\n\u4e0d\u904e\u6211\u770b\u7684\u662fCovid19 \u4e4b\u524d\u7684\u7248\u672c\uff0c\u500b\u4eba\u4e0d\u662f\u5f88\u60f3\u8981\u770b\u5230\u8001\u5e2b\u5b78\u751f\u90fd\u6234\u8457\u53e3\u7f69\uff0c\u5168\u7dda\u4e0a\u4e0a\u8ab2\u5c0d\u65bc\u6211\u4f86\u8aaa\uff0c\u6bd4\u8f03\u7f3a\u4e4f\u73fe\u5834\u4e92\u52d5\u7684\u611f\u89ba\u3002\\n:::\\n\\n[Vue - The Complete Guide (incl. Router & Composition API)](https://www.udemy.com/share/101WzM3@6HW3Ob704XQ--248wfeFDYf5KUllp5Rv-mlGUtEeYY_200uOip9tACWr2z48vr0=/)\\n\\n:::note\\n\u8001\u5e2b\u8a2d\u8a08\u8ab2\u7a0b\u6642\u6eff\u4ed4\u7d30\u7684\uff0c\u8ab2\u7a0b\u4e2d\u63d0\u4f9b\u4e86\u9084\u672a\u5b8c\u6210\u7684\u7bc4\u4f8b\u7a0b\u5f0f\u78bc\u4f5c\u696d\uff0c\u548c\u5df2\u5b8c\u6210\u4f5c\u696d\u7684\u89e3\u7b54\u7a0b\u5f0f\u78bc\u4f9b\u53c3\u8003\uff0c\u8ab2\u7a0b\u4e5f\u662f step by step \u7684\u5f15\u5c0e\u5b78\u751f\u6162\u6162\u7406\u89e3\u529f\u80fd\u3002\\n\\n\u512a\u9ede\u662f\u4e0d\u6703\u88ab\u6642\u9593\u548c\u5730\u9ede\u6240\u9650\u5236\uff0c\u7f3a\u9ede\u662f\u7dda\u4e0a\u8ab2\u7a0b\u5bb9\u6613\u8b93\u4eba\u61c8\u6020\uff0c\u66fe\u4ee5\u70ba\u5be6\u9ad4\u8ab2\u7a0b\u6703\u6bd4\u7dda\u4e0a\u8ab2\u7a0b\u4f86\u7684\u5438\u6536\u529b\u597d\uff0c\u4f46\u4e0a\u5b8c\u9019\u500b Vue \u7684\u7dda\u4e0a\u8ab2\u7a0b\u5f8c\uff0c\u89ba\u5f97\u4e26\u4e0d\u6703\u905c\u65bc\u5be6\u9ad4\u8ab2\u7a0b\u3002\u7dda\u4e0a\u8ab2\u7a0b\u7684\u7f3a\u9ede\u662f\u6c92\u8fa6\u6cd5\u99ac\u4e0a\u8209\u624b\u554f\u8001\u5e2b\uff0c\u4e0d\u904e\u9019\u500b\u8ab2\u7a0b\u7684\u4f5c\u696d\u8a2d\u8a08\u7d30\u81a9\u5ea6\u662f\u5f88\u8db3\u5920\u7684\uff0c\u91dd\u5c0d\u4e0d\u61c2\u7684\u90e8\u5206\u4e5f\u53ef\u4ee5\u53cd\u8986\u89c0\u770b\uff0c\u8d81\u7279\u50f9\u6642\u8cb7\u4e5f\u53ea\u9700\u8981\u53f0\u5e63\u4e09\u767e\u591a\u584a\uff0c\u500b\u4eba\u89ba\u5f97\u5341\u5206\u5212\u7b97\uff0c\u503c\u5f97\u63a8\u85a6\u3002\\n:::\\n\\n[\u8a08\u7b97\u6a5f\u6982\u8ad6/\u4e8e\u5929\u7acb\u6559\u6388 - \u81fa\u5927\u79d1\u5b78\u6559\u80b2\u767c\u5c55\u4e2d\u5fc3CASE](https://youtube.com/playlist?list=PLil-R4o6jmGiDc1CC8PyBbasl8kR9r8Wr)\\n\\n:::note\\n\u56e0\u70ba\u6211\u662f\u975e\u672c\u79d1\u5c08\u696d\uff0c\u5c0d\u65bc\u8a08\u6982\u4e26\u4e0d\u662f\u975e\u5e38\u719f\u6089\uff0c\u5728\u53cb\u4eba\u7684\u63a8\u85a6\u4e0b\u770b\u4e8e\u5929\u7acb\u6559\u6388\u7684\u8a08\u6982\u7dda\u4e0a\u8ab2\u7a0b\uff0c\u89ba\u5f97\u53f0\u5927\u771f\u7684\u975e\u5e38\u4f5b\u5fc3\uff0c\u628a\u5b8c\u6574\u7684\u8ab2\u7a0b\u653e\u5230\u7db2\u8def\u4e0a\u514d\u8cbb\u8b93\u4eba\u5b78\u7fd2\uff0c\u8001\u5e2b\u53e3\u689d\u6e05\u695a\uff0c\u800c\u4e14\u662f\u7e41\u9ad4\u4e2d\u6587\u7684\u5b78\u7fd2\u8cc7\u6e90\u3002\\n\\n\u7f3a\u9ede\u5927\u6982\u5c31\u662f\u5b78\u7fd2\u66f2\u7dda\u504f\u9ad8\uff0c\u5c0d\u65bc\u975e\u672c\u79d1\u751f\u6703\u7565\u5fae\u75db\u82e6\uff0c\u6211\u662f\u6311\u81ea\u5df1\u6bd4\u8f03\u6709\u53ef\u80fd\u6703\u63a5\u89f8\u5230\u7684\u90e8\u5206\u4f86\u770b\uff0c\u6709\u4e9b\u592a\u96fb\u6a5f\u7684\u90e8\u5206\u4e26\u6c92\u6709\u5b8c\u5168\u770b\u5b8c\uff0c\u6709\u4e9b\u7bc7\u5e45\u770b\u904e\u4e00\u6b21\u4e0d\u662f\u592a\u719f\u6089\uff0c\u4e4b\u5f8c\u61c9\u8a72\u6703\u518d\u53cd\u8986\u591a\u770b\u5e7e\u6b21\u3002\\n:::\\n\\n---\\n\\n## \u96fb\u5b50\u66f8\\n\\n[You Don\'t Know JS](https://github.com/getify/You-Dont-Know-JS)\\n\\n:::note\\n\u7b97\u662f JS \u8056\u7d93\u7b49\u7d1a\u7684\u66f8\uff0c\u5b8c\u5168\u514d\u8cbb\u9084\u6709\u4e2d\u6587\u7248\u672c\u53ef\u4ee5\u770b\uff0c\u6703\u89ba\u5f97\u8207\u5176\u4e00\u53e3\u6c23\u5543\u5b8c\uff0c\u4e0d\u5982\u5206\u6210\u5e7e\u500b\u6bb5\u843d\u770b\u5b8c\u4e00\u6b21\uff0c\u5982\u679c\u5728\u5be6\u4f5c\u4e0a\u6709\u9047\u5230\u76f8\u95dc\u554f\u984c\uff0c\u518d\u56de\u982d\u91cd\u65b0\u770b\u76f8\u95dc\u7684\u7ae0\u7bc0\uff0c\u6703\u6bd4\u4e00\u6b21\u57cb\u982d\u731b K \u5b8c\u3127\u6574\u672c\u66f4\u6709\u611f\u3002\\n:::\\n\\n---\\n\\n## \u7db2\u7ad9\\n\\n- [State of JS 2022](https://2022.stateofjs.com/en-US/)\\n- [State of CSS 2022](https://2022.stateofcss.com/en-US/)\\n\\n:::note\\nstate of \u7cfb\u5217\u7db2\u7ad9\u6211\u5f9e\u524d\u5e7e\u5e74\u958b\u59cb\u8ffd\uff0c\u6bcf\u4e00\u5e74\u7684\u5e74\u672b\u5230\u9694\u5e74\u7684\u5e74\u521d\u6703\u63a8\u51fa\u8a72\u5e74\u7684\u7e3d\u56de\u9867\uff0c\u5f9e\u9019\u4efd\u8abf\u67e5\u5831\u544a\u88e1\u53ef\u4ee5\u770b\u51fa\uff0c\u73fe\u5728\u6d41\u884c\u8da8\u52e2\u662f\u4ec0\u9ebc\uff0c\u54ea\u4e9b\u6846\u67b6/\u8a9e\u6cd5/\u5de5\u5177\u6700\u591a\u4eba\u63a8\uff0c\u6709\u54ea\u4e9b\u65b0\u7684\u6846\u67b6/\u8a9e\u6cd5/\u5de5\u5177\u7b49\u7b49...\u53ef\u4ee5\u4e86\u89e3\u6574\u500b JS / CSS \u7684\u8da8\u52e2\u8108\u52d5\u3002\u7f3a\u9ede\u662f\u9019\u662f\u5728\u7db2\u8def\u4e0a\u666e\u767c\u82f1\u6587\u554f\u5377\u7684\u8abf\u67e5\uff0c\u6240\u4ee5\u6a23\u672c\u6578\u591a\u70ba\u6b50\u7f8e\u767d\u4eba\uff0c\u50cf\u662f\u4e00\u4e9b\u6846\u67b6\u7684\u9078\u64c7\u3001\u85aa\u6c34\u7684\u7d1a\u8ddd\u7b49\uff0c\u5c0d\u65bc\u76ee\u524d\u8eab\u8655\u4e9e\u6d32\u7684\u6211\u53ea\u80fd\u914c\u91cf\u53c3\u8003\uff0c\u4e0d\u904e\uff0c\u5982\u679c\u662f\u76ee\u6a19\u64fa\u5728\u6b50\u7f8e\u8077\u7f3a\u7684\u8a71\uff0c\u7528\u9019\u4efd\u8abf\u67e5\u5831\u544a\u4f86\u6aa2\u9a57\u81ea\u5df1\u7684\u80fd\u529b\u662f\u5426\u7b26\u5408\u6b50\u7f8e\u8077\u7f3a\u7684\u671f\u5f85\uff0c\u662f\u6eff\u9069\u5408\u7684\u3002\\n:::\\n\\n---\\n\\n## Podcast\\n\\n[Syntax. - A tasty treats podcast for Web Developers.](https://syntax.fm/)\\n\\n:::note\\n\u4e4b\u524d\u5c31\u6709\u5728\u770b Scott Tolinski \u7684 [Level Up Tuts \u6559\u7a0b](https://www.youtube.com/c/leveluptuts) \u548c Wes Bos \u7684 [JavaScript 30](https://javascript30.com/)\uff0c\u5f8c\u4f86\u767c\u73fe\u4ed6\u5011\u4e00\u8d77\u5408\u4f5c Podcast\uff0c\u7406\u6240\u7576\u7136\u5c31\u4f86\u500b\u4e00\u9375\u4e09\u9023\u6309\u8b9a\u8a02\u95b1\u5206\u4eab\uff1a\uff09\\n\\n\u4ed6\u5011\u7684 Podcast \u6574\u9ad4\u57fa\u8abf\u7b97\u662f\u8f15\u9b06\u804a\u5929\u7684\u6c1b\u570d\uff0c\u4e0d\u662f\u975e\u5e38\u6b7b\u677f\u6216\u786c\u6838\u5230\u8b93\u4eba\u96e3\u4ee5\u6d88\u5316\uff0c\u4e0d\u904e\uff0c\u57fa\u672c\u4e0a\u6211\u4e26\u6c92\u6709\u6bcf\u96c6\u90fd\u807d\uff0c\u662f\u6311\u81ea\u5df1\u559c\u6b61\u7684\u4e3b\u984c\u9078\u8457\u807d\uff0c\u770b\u770b\u76f8\u540c\u9818\u57df\u7684\u4eba\u5c0d\u65bc\u6280\u8853\u6216\u5de5\u5177\u767c\u8868\u4e00\u4e9b\u60f3\u6cd5\uff0c\u662f\u6eff\u6709\u8da3\u800c\u4e14\u53ef\u4ee5\u7372\u5f97\u65b0\u77e5\u3002\u63a8\u85a6\u7d66\u60f3\u8981\u6642\u4e0d\u6642\u4e86\u89e3\u4e00\u4e0b\u65b0\u6280\u8853\u65b0\u77e5\u8b58\uff0c\u4f46\u76ee\u524d\u9084\u6c92\u6709\u9069\u5408\u7ba1\u9053\u6216\u540c\u597d\u53ef\u4ee5\u8a0e\u8ad6\u7684\u4eba\uff0c\u8f15\u9b06\u7684\u7bc0\u594f\u662f\u53ef\u4ee5\u8b93\u4eba\u6bd4\u8f03\u7121\u58d3\u529b\u7684\u807d\u3002\\n:::"},{"id":"git-memo","metadata":{"permalink":"/blog/git-memo","source":"@site/blog/2023-03-02-git-memo.mdx","title":"Git Memo","description":"git hooks post-receive shell","date":"2023-03-02T00:00:00.000Z","formattedDate":"March 2, 2023","tags":[{"label":"git","permalink":"/blog/tags/git"}],"readingTime":0.545,"hasTruncateMarker":false,"authors":[{"name":"Zoe Huang","title":"F2E x Sweetholic x Backpacker","url":"https://typeii8.github.io","imageURL":"/img/avatar.jpg","key":"zoe"}],"frontMatter":{"slug":"git-memo","title":"Git Memo","authors":"zoe","tags":["git"]},"prevItem":{"title":"Frontend \u5b78\u7fd2\u8cc7\u6e90","permalink":"/blog/f2e-resource"},"nextItem":{"title":"UI/UX \u5b78\u7fd2\u8cc7\u6e90","permalink":"/blog/uiux-resource"}},"content":"## git hooks post-receive shell\\n\\n```shell\\n#!/bin/sh\\ngit_folder=/usr/local/apache-tomcat-8.5.46-9487/webapps/repo\\ntomcat_folder=/usr/local/apache-tomcat-8.5.46-9487/webapps/ROOT\\nrm -rf ${git_folder}\\nmkdir ${git_folder}\\ncd ${git_folder}\\ngit --work-tree=${git_folder} --git-dir=/var/repo/qa.git checkout -f master\\nrm -rf src\\ncp -r ${git_folder}/dist/image/ ${tomcat_folder}/\\ncp -r ${git_folder}/dist/css/ ${tomcat_folder}/\\ncp ${git_folder}/dist/js/main.js ${tomcat_folder}/js/\\n```\\n\\n---\\n\\n## Git \u5927\u5c0f\u5beb\u8a2d\u5b9a\\n\\n\u95dc\u9589\u55ae\u4e00\u5c08\u6848\u7684\u5927\u5c0f\u5beb\u5ffd\u7565\u8a2d\u5b9a (\u7b49\u540c\u65bc\u958b\u555f\u5927\u5c0f\u5beb\u5075\u6e2c)\\n\\n```shell\\n$ git config --local core.ignorecase false\\n```\\n\\n\u958b\u555f\u55ae\u4e00\u5c08\u6848\u7684\u5927\u5c0f\u5beb\u5ffd\u7565\u8a2d\u5b9a (\u7b49\u540c\u65bc\u95dc\u9589\u5927\u5c0f\u5beb\u5075\u6e2c)\\n\\n```shell\\n$ git config --local core.ignorecase true\\n```"},{"id":"uiux-resource","metadata":{"permalink":"/blog/uiux-resource","source":"@site/blog/2023-02-28-uiux-resource.mdx","title":"UI/UX \u5b78\u7fd2\u8cc7\u6e90","description":"\u5728\u81ea\u5b78\u7684\u8def\u4e0a\uff0c\u9762\u5c0d\u5404\u7a2e\u5927\u91cf\u7684\u8cc7\u8a0a\uff0c\u6709\u4e00\u4e9b\u662f\u81ea\u5df1\u63a5\u89f8\u904e\uff0c\u89ba\u5f97\u503c\u5f97\u63a8\u85a6\u5206\u4eab\u7684\u8cc7\u6e90\uff0c\u5728\u9019\u88e1\u62cb\u78da\u5f15\u7389\uff0c\u90e8\u5206\u4e5f\u9644\u4e0a\u81ea\u5df1\u7684\u5fc3\u5f97\u77ed\u8a55\uff0c\u5e0c\u671b\u80fd\u5920\u63d0\u4f9b\u7d66\u540c\u6a23\u66fe\u7d93\u8ff7\u60d8\u7684\u540c\u597d\uff0c\u4f5c\u70ba\u53c3\u8003\uff1a","date":"2023-02-28T00:00:00.000Z","formattedDate":"February 28, 2023","tags":[{"label":"uiux","permalink":"/blog/tags/uiux"},{"label":"resource","permalink":"/blog/tags/resource"}],"readingTime":9.245,"hasTruncateMarker":false,"authors":[{"name":"Zoe Huang","title":"F2E x Sweetholic x Backpacker","url":"https://typeii8.github.io","imageURL":"/img/avatar.jpg","key":"zoe"}],"frontMatter":{"slug":"uiux-resource","title":"UI/UX \u5b78\u7fd2\u8cc7\u6e90","authors":"zoe","tags":["uiux","resource"]},"prevItem":{"title":"Git Memo","permalink":"/blog/git-memo"}},"content":"\u5728\u81ea\u5b78\u7684\u8def\u4e0a\uff0c\u9762\u5c0d\u5404\u7a2e\u5927\u91cf\u7684\u8cc7\u8a0a\uff0c\u6709\u4e00\u4e9b\u662f\u81ea\u5df1\u63a5\u89f8\u904e\uff0c\u89ba\u5f97\u503c\u5f97\u63a8\u85a6\u5206\u4eab\u7684\u8cc7\u6e90\uff0c\u5728\u9019\u88e1\u62cb\u78da\u5f15\u7389\uff0c\u90e8\u5206\u4e5f\u9644\u4e0a\u81ea\u5df1\u7684\u5fc3\u5f97\u77ed\u8a55\uff0c\u5e0c\u671b\u80fd\u5920\u63d0\u4f9b\u7d66\u540c\u6a23\u66fe\u7d93\u8ff7\u60d8\u7684\u540c\u597d\uff0c\u4f5c\u70ba\u53c3\u8003\uff1a\\n\\n## \u597d\u66f8\u5171\u8b80\\n\\n[\u8a8d\u77e5\u8207\u8a2d\u8a08-\u7406\u89e3UI\u8a2d\u8a08\u6e96\u5247](http://www.books.com.tw/products/CN11145346)\\n\\n:::note\\n\u5176\u5be6\u8001\u65e9\u5c31\u6392\u4e86\u597d\u5ea6\u8981\u5beb\u9019\u672c\u66f8\u7684\u8b80\u5f8c\u611f\uff0c\u537b\u96e3\u4ee5\u4e0b\u7b46\uff0c\u7d55\u975e\u9019\u672c\u66f8\u7684\u5167\u5bb9\u6216\u67b6\u69cb\u54ea\u908a\u6709\u554f\u984c\uff0c\u800c\u662f\u5f88\u6015\u81ea\u5df1\u5beb\u4e0d\u597d\uff0c\u7121\u6cd5\u5145\u5206\u8868\u9054\u9019\u672c\u66f8\u7684\u512a\u9ede\uff0c\u6700\u5f8c\u60f3\u60f3\uff0c\u521d\u8877\u4e0d\u5c31\u662f\u8981\u8aa0\u5be6\u8a18\u9304\u81ea\u5df1\u8b80\u5b8c\u9019\u672c\u66f8\u7684\u611f\u60f3\u8ddf\u555f\u767c\uff0c\u4f55\u5fc5\u5982\u6b64\u7cfe\u7d50\u3002\\n\\n**\u7e3d\u7d50\u4f86\u8aaa\uff0c\u9019\u662fUI Design/Web Design\u7684\u5fc5\u8b80\u7d93\u5178**\\n\\nUI Design\u76f8\u7e7c\u5176\u4ed6\uff0c\u7b97\u662f\u8f03\u70ba\u65b0\u8d77\u7684\u9818\u57df\uff0c\u4ee5\u524d\u81ea\u5df1\u7e3d\u89ba\u5f97\u627e\u4e0d\u5230\u76f8\u95dc\u8cc7\u6599\u53ef\u4ee5\u95b1\u8b80\uff0c\u7121\u5f9e\u5165\u9580\uff08\u723e\u5f8c\u624d\u6162\u6162\u767c\u73fe\u662f\u81ea\u5df1\u898b\u8b58\u592a\u5c11\uff0c\u4e0d\u77e5\u9053\u8a72\u600e\u9ebc\u53bb\u6316\u8cc7\u6599\uff09\u3002\u82e5\u60f3\u52a0\u5f37UI Design\u7684\u57fa\u790e\u77e5\u8b58\uff0c\u63a8\u85a6\u597d\u597d\u8b80\u904e\u5e7e\u6b21\u9019\u672c\u66f8\uff0c\u628a\u6e96\u5247\u770b\u719f\u7262\u8a18\u5728\u5fc3\uff0c\u5f80\u5f8c\u53ef\u65bc\u5be6\u4f5c\u6642\u907f\u8a31\u591a\u57fa\u672c\u932f\u8aa4\u3002\u9019\u672c\u66f8\u5f88\u9069\u5408\u4e00\u8b80\u518d\u8b80\uff0c\u6216\u7576\u5de5\u5177\u66f8\u7ffb\u95b1\uff0c\u4e0d\u8ad6\u662f\u521d\u5b78\u8005\u7528\u4f86\u6253\u5e95\uff0c\u9032\u968e\u8005\u7528\u4f86\u52a0\u5f37\u6982\u5ff5\uff0c\u534a\u8def\u51fa\u5bb6\u8005\u60f3\u641e\u61c2UI Design\u662f\u600e\u9ebc\u4e00\u56de\u4e8b\uff0c\u8001\u5c11\u54b8\u5b9c\u3002\\n\\n\u9019\u672c\u66f8\u5f9e\u6700\u57fa\u672c\u7684\u5b9a\u7fa9\u95e1\u8ff0\u8d77\uff0c\u5217\u51fa\u8a31\u591a\u73fe\u8c61\u6216\u523b\u677f\u5370\u8c61\uff0c\u4ee5\u79d1\u5b78\u5be6\u9a57\u70ba\u57fa\u790e\uff0c\u884d\u4f38\u5b8c\u6574\u7684\u7406\u8ad6\u53bb\u4f50\u8b49\u73fe\u8c61\u6216\u63a8\u7ffb\u523b\u677f\u5370\u8c61\u3002\u5176\u5be6\u6bcf\u500b\u7ae0\u7bc0\u6211\u90fd\u89ba\u5f97\u5f88\u6709\u7528\uff0c\u53ef\u4ee5\u62ff\u51fa\u4f86hilight \uff0c\u6bcf\u500b\u6e96\u5247\u5f8c\u9762\u90fd\u5176\u7406\u8ad6\u4f9d\u64da\uff0c\u800c\u4e0d\u662f\u7528\u201c\u9019\u6a23\u770b\u8d77\u4f86\u6bd4\u8f03\u597d\u770b\u201d\u6216\u201c\u5927\u5bb6\u90fd\u9019\u6a23\u505a\u201d\u5e36\u904e\u3002\u81ea\u5df1\u5370\u8c61\u5f88\u6df1\u523b\u7684\u662f\uff0c\u6709\u4e00\u6bb5\u63d0\u53ca\u4eba\u985e\u5927\u8166\u7684\u53cd\u61c9\u6642\u9593\uff0c\u5957\u7528\u65bcUI Design\uff0c\u4fbf\u662f\u8981\u53bb\u601d\u8003\u8a72\u5982\u4f55\u8a2d\u8a08\u7522\u54c1\u5728\u5927\u8166\u53cd\u61c9\u6642\u9593\u5167\u7d66\u4e88\u9069\u7576\u56de\u994b\uff0c\u6216\u662f\u8981\u600e\u9ebc\u505a\u624d\u4e0d\u6703\u8b93\u4f7f\u7528\u8005\u7522\u751f\u6df7\u6dc6\u611f\u3002\u9644\u9304\u9084\u8cbc\u5fc3\u5730\u6536\u9304\u4e86\u8457\u540dUI Design\u6e96\u5247\uff0c\u6574\u672c\u66f8\u7684\u7cbe\u83ef\u90fd\u5217\u9ede\u65bc\u6b64\uff0c\u4e0d\u5f71\u5370\u4e0b\u4f86\u8cbc\u5728\u66f8\u684c\u524d\u5929\u5929\u6717\u8aa6\u90fd\u4e0d\u884c\u3002\\n:::\\n\\n\\n[\u773e\u5999\u4e4b\u9580\ufe30\u7db2\u7ad9UI\u8a2d\u8a08\u4e4b\u9053](http://www.books.com.tw/products/CN10708290)\\n\\n:::note\\n\u6700\u521d\u60f3\u8aaa\u770b\u7c21\u4e2d\u7248\u61c9\u8a72\u6703\u6bd4\u8f03\u5bb9\u6613\u77ad\u89e3\u6982\u5ff5\uff0c\u770b\u5b8c\u4e00\u904d\u4ee5\u5f8c\u7684\u611f\u60f3\u662f\u9084\u662f\u4e56\u4e56\u518d\u5543\u4e00\u6b21\u82f1\u6587\u6bd4\u8f03\u5be6\u5728...\u7562\u7adf\u5f88\u591a\u8853\u8a9e\u8ddf\u7a0b\u5f0f\u8a9e\u8a00\u90fd\u662f\u82f1\u6587\uff0c\u5c08\u696d\u8853\u8a9e\u88ab\u7ffb\u6210\u7c21\u4e2d\u7528\u6cd5\u53cd\u800c\u4e00\u6642\u4e4b\u9593\u7121\u6cd5\u6703\u610f\uff0c\u5982\u679c\u662f\u6015\u81ea\u5df1\u82f1\u6587\u7a0b\u5ea6\u4e0d\u5920\u6216\u95b1\u8b80\u901f\u5ea6\u592a\u6162\uff0c\u63a8\u85a6\u5148\u8b80\u4e00\u6b21\u7c21\u4e2d\u6293\u4f4f\u4e3b\u8ef8\u6982\u5ff5\uff0c\u7136\u5f8c\u518d\u8b80\u4e00\u6b21\u539f\u6587\uff0c\u78ba\u8a8d\u5c08\u696d\u8853\u8a9e\u548c\u7d30\u7bc0\u3002\\n\\n\u7e3d\u7d50\u4f86\u8aaa\uff0c\u5927\u63a8\uff01\u5728\u9019\u500b\u9818\u57df\u6df7\u7684\u81f3\u5c11\u8981\u8b80\u904e\uff0c\u66f8\u4e2d\u5e7e\u4e4e\u628a\u7db2\u9801\u8a2d\u8a08\u7684\u57fa\u672c\u6982\u5ff5\u90fd\u8b1b\u904e\u4e00\u8f2a\uff0c\u67d0\u4e9b\u90e8\u4efd\u9817\u70ba\u6df1\u5165\uff0c\u9664\u4e86\u6982\u5ff5\u63a2\u8a0e\u751a\u81f3\u727d\u6d89\u5230\u7a0b\u5f0f\uff0c\u9069\u5408\u7d66\u5c0d\u7db2\u9801\u8a2d\u8a08\u5df2\u7d93\u6709\u4e00\u4e9b\u5e95\u5b50\u7684\u8a2d\u8a08\u5e2b/\u5de5\u7a0b\u5e2b\u8b80\u3002\u89ba\u5f97Smashing magazine\u662f\u500b\u5f88\u68d2\u7684\u7d9c\u5408\u77e5\u8b58\u7db2\u7ad9\uff0c\u5f9eweb design,coding\u5230UI/UX\u90fd\u6709\u8c50\u5bcc\u7684\u77e5\u8b58\u548c\u6280\u8853\u5206\u4eab\uff0c\u9664\u4e86\u8a02\u95b1\u96fb\u5b50\u5831follow\u6700\u65b0\u6f6e\u7684\u89c0\u5ff5\u4e4b\u5916\uff0c\u770b\u5e7e\u672c\u6982\u5ff5\u66f8\u52a0\u5f37\u57fa\u790e\u4e5f\u5f88\u4e0d\u932f\uff0c\u5982\u679c\u4e0d\u77e5\u9053\u8981\u5f9e\u54ea\u908a\u8d77\u982d\uff0c\u63a8\u85a6\u76f4\u63a5\u8b80\u5e7e\u672cSmashing book\u6253\u5e95\u3002\\n\\n\u9019\u672c\u66f8\u7684\u524d\u534a\u90e8\u6bd4\u8f03\u504f\u91cdCSS\u8ddfLayout\uff0c\u500b\u4eba\u89ba\u5f97\u5f88\u6709\u7528\u7684\u662f\u95dc\u65bc\u5b57\u9ad4\u5c0d\u65bc\u7248\u9762\u69cb\u6210\u7684\u5f71\u97ff\uff0c\u81ea\u5df1\u4ee5\u5f80\u7684\u7d93\u9a57\u5176\u5be6\u5c0d\u65bc\u4f7f\u7528\u54ea\u7a2e\u5b57\u9ad4\u76f8\u4e92\u914d\u5408\uff0c\u4e26\u6c92\u6709\u771f\u6b63\u6df1\u5165\u63a2\u7a76\uff0c\u770b\u4e86\u624d\u77e5\u9053\u5b57\u9ad4\u5176\u5be6\u4e5f\u662f\u9580\u5f88\u6df1\u7684\u5b78\u554f\uff0c\u5149\u662f\u896f\u9ad4\u8207\u5426\u5c31\u770b\u5230\u773c\u82b1\u64a9\u4e82\uff0c\u4e0b\u6b21\u57f7\u884c\u5c08\u6848\u6253\u7b97\u597d\u597d\u914d\u5408\u9019\u500b\u90e8\u5206\u7684\u7406\u8ad6\uff0c\u53bb\u6311\u9078\u9069\u5408\u7684\u5b57\u9ad4\u3002\\n\\n\u4e2d\u5f8c\u534a\u90e8\u8f03\u591a\u7a0b\u5f0f\u6216\u6982\u5ff5\u5206\u4eab\uff0c\u4f8b\u5982\u5728\u8a2d\u8a08\u7248\u9762\u6642\u61c9\u8a72\u8981\u53c3\u8003\u9075\u5b88\u54ea\u4e9b\u6e96\u5247\u7b49\u7b49\u3002\u6709\u5e7e\u500b\u5730\u65b9\u6211\u89ba\u5f97\u5f88\u53d7\u7528\uff0c\u50cf\u662f\u201c\u5bb9\u932f\u6027\u201d\u7684\u91cd\u8981\uff0c\u5fc5\u9808\u8b93\u4f7f\u7528\u8005\u53ef\u4ee5\u76f4\u89ba\u4e14\u6d41\u66a2\u7684\u64cd\u4f5c\uff0c\u4e14\u907f\u514d\u4e0d\u53ef\u9006\u7684\u64cd\u4f5c\u767c\u751f\u3002\u6216\u662f\u5728\u8a2d\u8a08\u7db2\u7ad9\u6642\uff0c\u8a2d\u8a08\u4eba\u54e1\u61c9\u8a72\u8981\u7ad9\u5728\u4f7f\u7528\u8005\u89d2\u5ea6\u53bb\u69cb\u601d\u767c\u60f3\uff0c\u800c\u4e0d\u662f\u4ee5\u81ea\u5df1\u8eab\u70ba\\"\u8a2d\u8a08\u5e2b\\"\u6216\\"\u5de5\u7a0b\u5e2b\\"\u7684\u89d2\u8272\u51fa\u767c\uff0c\u7562\u7adf\u7d55\u5927\u90e8\u5206\u662f\u8981\u958b\u767c\u529f\u80fd\u6027\u7db2\u7ad9\u7d66\u7279\u5b9a\u65cf\u7fa4\u4f7f\u7528\uff0c\u8981\u5b8c\u5168\u5c55\u73fe\u81ea\u5df1\u7684\u60f3\u6cd5\u8ddf\u8a2d\u8a08\u7406\u5ff5\uff0c\u628a\u9019\u4e9b\u6771\u897f\u7559\u7d66\u500b\u4ebaportfolio\u5c31\u53ef\u4ee5\u4e86\u3002\\n\\n\u7455\u4e0d\u63a9\u745c\u4e4b\u8655\uff1a\u7562\u7adf\u51fa\u7248\u5e74\u4efd\u7a0d\u65e9\uff0c\u7bc4\u4f8b\u7db2\u7ad9\u7684\u98a8\u683c\u548c\u6a23\u5f0f\u8ddf\u76ee\u524d\u6700\u6d41\u884c\u6700\u524d\u7aef\u7684\u6709\u4e9b\u5dee\u8ddd\uff0c\u4e0d\u904e\u4e26\u4e0d\u5f71\u97ff\u6e96\u5247\u8ddf\u6982\u5ff5\u7684\u61c9\u7528\u3002\u6216\u662f\u95dc\u65bcRWD\u8ddfAWD\uff0c\u751a\u81f3\u662fCSS framework\u7684\u9078\u64c7\uff0c\u73fe\u4eca\u6709\u8a31\u591a\u5353\u8d8a\u7684\u6280\u8853\u548cframework\u53ef\u4f9b\u9078\u64c7\uff08\u50cf\u9f0e\u9f0e\u6709\u540d\u7684Bootstrap\uff09\uff0c\u6709\u4e9b\u7591\u616e\u6216\u662f\u6280\u8853\u6027\u9700\u514b\u670d\u7684\u5730\u65b9\u5df2\u7372\u5f97\u89e3\u6c7a\uff0c\u4e0d\u904e\u540c\u6a23\u7684\uff0c\u9019\u4e9b\u4e26\u4e0d\u6703\u8ddf\u539f\u5247\u6216\u6982\u5ff5\u76f8\u885d\u7a81\uff0c\u5728\u95b1\u8b80\u904e\u7a0b\u4e2d\uff0c\u6216\u8a31\u80fd\u9806\u4fbf\u60f3\u60f3\u73fe\u5728\u80fd\u7528\u54ea\u4e9b\u5de5\u5177\u89e3\u6c7a\u9019\u4e9b\u554f\u984c\u3002\\n\\n#### Thinking \\n\\n\u5728\u63a2\u8a0e\u5b57\u9ad4\u8ddf\u6392\u7248\u7684\u7ae0\u7bc0\uff0c\u6709\u4e00\u6bb5\u63d0\u5230\u9023\u6587\u53e5\u7684\u5782\u76f4\u5c0d\u9f4a\u90fd\u5fc5\u9808\u8003\u616e\uff0c\u662f\u5426\u4e0d\u540c\u5340\u584a\u7684\u6bb5\u843d\u6bcf\u4e00\u884c\u90fd\u80fd\u5c0d\u9f4a\u3002\u8a66\u8457\u5047\u60f3\u4e00\u4e0b\uff0c\u771f\u5fc3\u89ba\u5f97\u9019\u662f\u4e00\u500b\u8cbb\u5de5\u7684\u9b54\u9b3c\u7d30\u7bc0\uff0c\u53ef\u80fd\u8abf\u6821\u500b\u8001\u534a\u5929\uff0c\u4f7f\u7528\u8005\u6839\u672c\u7121\u611f\uff0c\u6216\u662f\u6295\u4e0b\u53bb\u7684\u529f\u8ddf\u9054\u5230\u7684\u6210\u6548\u4e0d\u6210\u6bd4\u4f8b\uff0c\u5c24\u5176\u662f\u53c8\u5728\u5c08\u6848\u6642\u9593\u58d3\u529b\u7dca\u8feb\u7684\u6642\u5019\uff0c\u9019\u4ef6\u4e8b\u60c5\u7684\u53ef\u884c\u6027\u548cCP\u503c\u5230\u5e95\u80fd\u5230\u54ea\u7a2e\u7a0b\u5ea6\uff0c\u771f\u662f\u500b\u554f\u865f...\\n\\n\u66f8\u88e1\u63d0\u53ca\u8a31\u591a\u9762\u5411\uff0c\u7684\u78ba\u6709\u5f88\u591a\u5de5\u5177\u80fd\u61c9\u7528\uff0c\u7684\u78ba\u6709\u8a31\u591a\u7d30\u7bc0\u9808\u8abf\u6821\uff0c\u6709\u8a31\u591a\u6d41\u7a0b\u548c\u5167\u5bb9\u8981\u69cb\u601d\u3002\u4f46\u8aaa\u5230\u5e95\uff0c\u82e5\u5728\u57f7\u884c\u5c08\u6848\u6642\uff0c\u74b0\u5883\u6216\u4eba\u70ba\u56e0\u7d20\u5f71\u97ff\uff0c\u8b93\u57f7\u884c\u8005\u96e3\u4ee5\u9054\u5230\u9019\u4e9b\u6a19\u6e96\uff0c\u8a72\u600e\u9ebc\u53bb\u62ff\u634f\u53d6\u6368\uff0c\u597d\u50cf\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002\\n:::\\n\\n\\n[\u9ede\u77f3\u6210\u91d1\uff1a\u8a2a\u5ba2\u81f3\u4e0a\u7684\u7db2\u9801\u8a2d\u8a08\u79d8\u7b08\uff08Don\u2228t Make Me Think\uff09](https://www.books.com.tw/products/CN10590461)\\n\\n[\u8cc7\u8a0a\u67b6\u69cb\u5b78\uff1a\u7db2\u7ad9\u3001App\u8207\u8cc7\u8a0a\u751f\u614b\u7cfb\u7d71\u898f\u5283](https://www.books.com.tw/products/0010774361)\\n\\n---\\n\\n## \u7dda\u4e0a\u8ab2\u7a0b\\n\\n[Coursea - Scott Klemmer / University of California San Diego](https://www.coursera.org/instructor/~1250)\\n\\n:::note\\n\u514d\u8cbb\u7684\u7dda\u4e0a\u8ab2\u7a0b\uff0c\u8001\u5e2b\u8b1b\u8a71\u5e73\u7de9\u3001\u8a9e\u901f\u504f\u6162\uff0c\u53ef\u4ee5\u4e86\u89e3\u7406\u8ad6\u57fa\u790e\uff0c\u9069\u5408\u521d\u5b78\u60f3\u63a5\u89f8UIUX\u9818\u57df\u8005\uff0c\u4e0d\u904e\u8ab2\u7a0b\u7565\u5fae\u50ac\u7720\uff0c\u63a8\u85a6\u53ef\u4ee5\u76f4\u63a5\u627e\u81ea\u5df1\u60f3\u8981\u4e86\u89e3\u7684\u90e8\u5206\u807d\\n:::\\n\\n---\\n\\n## \u793e\u7fa4/\u8b1b\u5ea7/\u6f14\u8a0e\u6703\\n\\n- [\u60a0\u8b58\u5b78\u9662](https://edu.userxper.com/)\\n- [HPX \u793e\u7fa4](https://hpx.tw/)\\n- [MIX \u5275\u65b0\u8a2d\u8a08\u5e74\u6703](https://mixconf.tw/)\\n\\n:::note\\n\u7b97\u662f\u540c\u4e00\u5718\u9ad4\u7d93\u71df\u7684\u4e00\u7cfb\u5217\u793e\u7fa4\uff0c\u6709\u5206\u4eab\u6027\u8cea\u7684\u5c0f\u578b\u8b1b\u5ea7\uff0c\u7dda\u4e0a\u793e\u7fa4\u548c\u8f03\u70ba\u6b63\u5f0f\u7684\u7814\u8a0e\u6703\uff0c\u5982\u679c\u662f\u60f3\u8981\u4e86\u89e3\u6700\u8fd1\u53f0\u7063\u5728\u6d41\u884c\u600e\u6a23\u7684UIUX\u77e5\u8b58\uff0c\u662f\u4e00\u500b\u6eff\u63a8\u85a6\u7684\u63a5\u89f8\u7ba1\u9053\u3002\\n\\n\u53ef\u60dc\u56e0\u70ba\u75ab\u60c5\u5f71\u97ff\uff0c\u9019\u5e7e\u5e74\u7684\u5be6\u9ad4\u6d3b\u52d5\u6bd4\u8f03\u5c11\uff0c\u8b1b\u5ea7\u500b\u4eba\u9084\u662f\u504f\u597d\u53c3\u52a0\u5be6\u9ad4\uff0c\u6bd4\u8f03\u6709\u4e92\u52d5\uff0c\u63a8\u85a6\u53ef\u9396\u5b9a\u81ea\u5df1\u6709\u8208\u8da3\u7684\u8b1b\u8005/\u4e3b\u984c\uff0c\u53c3\u52a0\u76f8\u95dc\u8b1b\u5ea7\u3002\\n:::"}]}')}}]);